import React from 'react'
import { Col, Row, Container } from "react-bootstrap";

const ItemThumbnails = () => {
    return (
        <>
        <Container>
            <Row className="preview">
                <ul className="preview-thumbnail nav nav-tabs">
                  <li className="active">
                    <a data-target="#pic-1" data-toggle="tab">
                      <img src="https://placehold.it/200x200.jpg" />
                    </a>
                  </li>
                  <li>
                    <a data-target="#pic-2" data-toggle="tab">
                      <img src="https://via.placeholder.com/650/9f9f9f" />
                    </a>
                  </li>
                  <li>
                    <a data-target="#pic-3" data-toggle="tab">
                      <img src="https://via.placeholder.com/650/9f9f9f" />
                    </a>
                  </li>
                  <li>
                    <a data-target="#pic-4" data-toggle="tab">
                      <img src="https://via.placeholder.com/650/9f9f9f" />
                    </a>
                  </li>
                </ul>
              </Row>  
        </Container>
        <style jsx>{`
        .preview {
            display: -webkit-box;
            display: -webkit-flex;
            display: -ms-flexbox;
            display: flex;
            -webkit-box-orient: vertical;
            -webkit-box-direction: normal;
            -webkit-flex-direction: column;
            -ms-flex-direction: column;
            flex-direction: column;

          }
          @media screen and (max-width: 996px) {
            .preview {
              margin-bottom: 20px;
            }
          }

          .preview-pic {
            -webkit-box-flex: 1;
            -webkit-flex-grow: 1;
            -ms-flex-positive: 1;
            flex-grow: 1;
          }

          .preview-thumbnail.nav-tabs {
            border: none;
            gap: 1px;
            margin: 14px auto 7px auto;
            width: 92%;
          }
          .preview-thumbnail.nav-tabs li {
            width: 24%;
            margin:0 auto 0 auto;
            padding: 0 0.12em 0 0.12em;
          }
          .preview-thumbnail.nav-tabs li img {
            max-width: 100%;
            border-radius: 8px;
            display: block;
          }
          .preview-thumbnail.nav-tabs li a {
            padding: 0;
            margin: 0;
          }
          .preview-thumbnail.nav-tabs li:last-of-type {
            margin-right: 0;
          }

          .tab-content {
            overflow: hidden;
          }
          .tab-content img {
            width: 100%;
            -webkit-animation-name: opacity;
            animation-name: opacity;
            -webkit-animation-duration: 0.3s;
            animation-duration: 0.3s;
          }



          h3 {
            padding: 5px 7px 5px 0;
          }

          .landing {
            margin: 23px auto 0 auto;
            max-width: 88vw;
          }

          ul > li {
            margin-right: 25px;
            font-weight: lighter;
            cursor: pointer;
          }
          li.active {
            border-bottom: 3px solid orange;
          }
          .nav > li.active {
            box-shadow: 1px 2px 2px rgba(0,0,0,0.12);
            // border-bottom: 1px solid rgba(0,125,245,0.45);
            border-bottom: none;
          }
          .nav > li {
            padding-top: 3px;

          }

          .item-photo {
            display: flex;
            justify-content: center;
            align-items: center;
            border-right: 1px solid #f6f6f6;
          }
          .menu-items {
            list-style-type: none;
            font-size: 11px;
            display: inline-flex;
            margin-bottom: 0;
            margin-top: 20px;
          }
        `}
        </style>
        </>
    )
}

export default ItemThumbnails
